mapper.select:
 	select member_id
 	from member
	where member_id = #{member_id}

 	select member_email
 	from member
	where member_email = #{member_email}

 	SELECT COUNT(*)
	from member
	where member_id = #{member_id} and member_pw = #{member_pw};

 	SELECT COUNT(*)
	from member
	where member_name = #{member_name} and member_email = #{member_email};
